package Day04ConditionalStatements;

public class EvenOddNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	//write a program that will print even and odd numbers
		//2,4,6,8....
		//1,3,5,7.....
	// this method will only run with modulus
		//only numbers will divide by 2 to get odd and even numbers
		int number = 10;
		
		// if (number % 2!1)
		if ( number % 2==1 ) {
		
			System.out.println("even number");
		
	}
		
		else {
			System.out.println("odd number");
		}

}
}