package Day04ConditionalStatements;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	// if i have 20 dollars ,i can request for a taxi// 20 dollars the condition
	// else,i will take a bus
		//if() this is called if statement
		// if condition has to be true otherwise the main body between the brackets{} will not work
		//if condition will work only when the condition is true
		
		
	int wallet = 10;
	
	if(wallet <= 10) {
		
		System.out.println("i will ride the taxi");
		}
	//jvm will check else when if condition is false otherwise it will not
	
	else       {
System.out.println("i will ride the bus");
	
	}
	
	
	
	

}
	}
